import React from 'react';
import styled from "styled-components"



const Container = styled.div`
    width:100%;
    height:100vh;
    margin:0px;
    overflow: auto;
    display:flex;
    padding-top:20px;
    flex-direction:column;
    background: #E5E5E5;
`
const Title = styled.h1`
    text-align: center;
    color: white;
`
const CartContainer = styled.div `
    color: white;
    margin: 5% 10%;
    border-bottom: 1px solid black;
    border-top: 1px solid black;

`
const ProductContainer = styled.div`
    margin: 0 5%;
`
const Column = styled.td`
width: calc(100%/5);
height:20vh;
text-align:center;

`

const Table = styled.table`
    width:100%;
`
const StyledImg = styled.img`
height:90%;
width: 90%;
`

const StyledTableRow = styled.tr`
    padding: 3vh;
`
const CartList = props => {


    return (
        <Container>
            <Title>Votre panier</Title>
            <CartContainer>
            <Table>
                    <thead>
                        <tr>
                            <Column></Column>
                            <Column>Pizza</Column>
                            <Column>Quantity</Column>
                            <Column>Price</Column>
                            <Column></Column>
                        </tr>
                    </thead>
                    <tbody>
                        <StyledTableRow>
                            <Column>
                                <StyledImg src="https://images.pexels.com/photos/1049626/pexels-photo-1049626.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260"></StyledImg>
                            </Column>
                            <Column>Margaritha</Column>
                            <Column>1</Column>
                            <Column>9.99$</Column>
                            <Column>
                                <button>Supprimer</button>
                            </Column>
                        </StyledTableRow>
                    </tbody>
                </Table>
            </CartContainer>
        </Container>
    );
};

export default CartList;
