import React from 'react';
import styled from "styled-components"
import ProductCart from '../ProductCart'



const Container = styled.div`
    width:100%;
    height:100vh;
    margin:0px;
    overflow: auto;
    display:flex;
    padding-top:20px;
    flex-direction:column;
    background: #E5E5E5;
`
const Title = styled.h1`
    text-align: center;
`
const CartContainer = styled.div `

    margin: 5% 10%;
    border-bottom: 1px solid black;
    border-top: 1px solid black;

`

const Column = styled.td`
width: calc(100%/5);
height:20vh;
text-align:center;

`

const Table = styled.table`
    width:100%;
`



const CartList = props => {

    const cart = [
        {id:1, img: "https://images.pexels.com/photos/1049626/pexels-photo-1049626.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260",name: "Margharita", qty: 2, price: 7.99},
        {id:7, img: "https://images.pexels.com/photos/1082343/pexels-photo-1082343.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500 1x, https://images.pexels.com/photos/1082343/pexels-photo-1082343.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500 2x",name: "Regina", qty: 1, price: 11.99},
        {id:6, img: "img",name: "4 fromages", qty: 4, price: 10.99},
        {id:3, img: "img",name: "Végétarienne", qty: 2, price: 16.99},
    ]
    return (
        <Container>
            <Title>Votre panier</Title>
            <CartContainer>
            <Table>
                    <thead>
                        <tr>
                            <Column></Column>
                            <Column>Pizza</Column>
                            <Column>Quantity</Column>
                            <Column>Price</Column>
                            <Column></Column>
                        </tr>
                    </thead>
                    <tbody>
                    {cart.map(product => 
                        <ProductCart 
                            price= {product.price} 
                            img = {product.img} 
                            name = {product.name} 
                            qty={product.qty}

                            >

                            </ProductCart>

                    )}
                    </tbody>
                </Table>
            </CartContainer>
        </Container>
    );
};

export default CartList;
